$(document).ready(function(){$('a[data-target="#informarModal"]').on("click",function(a){a.preventDefault();a=$(this).attr("href");$.ajax({url:a,xhrFields:{withCredentials:!0},success:function(a){$("#informarModal .modal-body").html(a);a=$("#informarModal");bootstrap.Modal.getOrCreateInstance(a).show()}})})});